import React from 'react';
import './headerStyle.sass';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';

class HeaderClass extends React.Component{
  constructor(props){
    super(props);
    this.state={
        arrayLink:[
            {name:"Главная",link:"/"},
            {name:"Поиск",link:"/search"},
            {name:"Помощь",link:"/help"},
            {name:"Контакты",link:"/contacts"},
        ],
        activeLink:""

    }
  }

  componentDidMount(){
    let pathname = window.location.pathname
    this.setState({activeLink:pathname})
  }


  render(){

    return (
        <div className="header">
            <div className="logoContain">
                <Link to="/" className="logoLink">Restaurants</Link>
            </div>
            <div className="arrayLink">
                {
                    this.state.arrayLink.map((el,index)=>
                        <div key={el.name+index} className='linkContain'>
                            <Link to={el.link} onClick={(el)=>{this.setState({activeLink:el.link})}} className={this.state.activeLink === el.link ? "linkitem linkitem-active": "linkitem"}>{el.name}</Link>
                        </div>
                    )
                }
            </div>
        </div>
    );
  }
}


const Header = connect(
  (state) => ({
    mainReduser: state.mainReduser,
  }),
)(HeaderClass);
export default Header;
