import React from 'react';
import './headerStyle.sass';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';

class HeaderClass extends React.Component{
  constructor(props){
    super(props);
    this.state={
        arrayLink:[
            {name:"Главная",link:"/",isActive:true},
            {name:"Поиск",link:"/",isActive:false},
            {name:"Помощь",link:"/",isActive:false},
            {name:"Контакты",link:"/",isActive:false},
        ]
    }
  }
  componentDidMount(){
      debugger
      let parhname = window.location.pathname
  }

  render(){

    return (
        <div className="header">
            <div className="logoContain">
                <Link to="/" className="logoLink">Restaurants</Link>
            </div>
            <div className="arrayLink">
                {
                    this.state.arrayLink.map((el,index)=>
                        <div key={el.name+el.isActive+index} className='linkContain'>
                            <Link to={el.link} className="linkitem">{el.name}</Link>
                        </div>
                    )
                }
            </div>
        </div>
    );
  }
}


const Header = connect(
  (state) => ({
    mainReduser: state.mainReduser,
  }),
)(HeaderClass);
export default Header;
